import React, { Component } from 'react';
import * as THREE from 'three';
//import Orbitcontrols from 'three-orbitcontrols';
//import  './index.less'

class Three extends Component {

    componentDidMount() {
        this.init()
    }

    init = () => {
        const scene =  new THREE.Scene()
        const camera = new THREE.PerspectiveCamera( 75, this.mount.clientWidth / this.mount.clientHeight, 0.1, 1000 );
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        //点光源
        const point = new THREE.PointLight(0xffffff);
        point.position.set(400, 200, 300); //点光源位置
        // 通过add方法插入场景中，不插入的话，渲染的时候不会获取光源的信息进行光照计算
        scene.add(point); 
        this.scene = scene
        this.camera = camera
        this.renderer = renderer
        renderer.setSize(this.mount.clientWidth, this.mount.clientHeight );
        this.mount.appendChild( renderer.domElement );
        camera.position.z = 5;

        this.createCube()
        this.createLine()
        this.animate();

    }



    createCube = () => {
      const loader = new THREE.ImageLoader();
      loader.load('earth.jpg', function( texture ) {
      // 作为纹理，或直接使用TextureLoader
      const geometry = new THREE.BoxGeometry(1, 2, 1, 4);
      const material = new THREE.MeshBasicMaterial({color: 0x739783, map:texture});
      const cube = new THREE.Mesh(geometry, material);
      this.cube = cube
      this.scene.add(cube);
      // scene.add(earth);
      // 直接绘制在canvas上
      // var canvas =document.createElement('canvas');
      // var context = canvas.getContext('2d');
      // context.drawImage(texture, 100, 100);    
  })
      //const geometry = new THREE.BoxGeometry( 1, 2, 1, 4 );
      //const material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
      // const cube = new THREE.Mesh( geometry, material );
      // this.cube = cube
      // this.scene.add( cube );
    }

    createLine = () => {
      const material = new THREE.LineBasicMaterial({color: 0x0f00ff}) //定义线的材质
      const geometry = new THREE.Geometry()
      geometry.vertices.push(new THREE.Vector3(-2, 0, 0))
      geometry.vertices.push(new THREE.Vector3( 0, 2, 0) ); //相当于是从 将前两个坐标连成一条线
      // geometry.vertices.push(new THREE.Vector3( 2, 0, 0) );
      const line = new THREE.Line(geometry, material)
      this.line = line
      line.position.x = -1
      line.position.y = 2
      this.scene.add(line)
    }

    animate =() => {
      requestAnimationFrame( this.animate );
      this.cube.rotation.z += 0.01;
      this.cube.rotation.y += 0.01;
      this.line.rotation.x += 0.02
      this.renderer.render( this.scene, this.camera );
    }

    componentWillUnmount() {
        this.mount.removeChild(this.renderer.domElement)
      }
    render() {
        return (
            <div
                id= "canvas"
                style={{ width: '800px', height: '800px',background:'#000' }}
                ref={(mount) => { this.mount = mount }}
            />
        );
    }
}
//   ReactDOM.render(<Scene />, document.getElementById('canvas'))

export default Three;
