import React, {Component} from 'react';
import StudiesService from "../services/DicomService";
import SearchBar from "../components/SearchBar";
import PatientTable from "../components/PatientTable";


class PatientsPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            patients: [],
            filterText:'',//搜索时用的过滤词语
        };
        this.setState = this.setState.bind(this);
    }

    //更新过滤词
    handelFilter = (filterText) => {
        this.setState({
            filterText:filterText
        });
    };

    componentWillMount() {
        console.log("开始获取patient数据");
        StudiesService.findPatients(patients => {
            console.log("已经获取到如下patient数据");
            console.log(patients);
            this.setState({patients: patients});
            console.log(patients);
        });
    }

    render() {
        //console.log(this.state.patients);
        const patients = this.state.patients;
        return (
            <div>
                <div class="rowmenu">
                    <a class="patientitem" href="/patients">Patient</a>
                    <a class="studiesitem" href="/studies">Studies</a>
                </div>
                <h1>Patients</h1>
                <p>输入病人姓名关键字进行检索</p >
                <SearchBar 
                filterText={this.state.filterText}
                onFilterChange={this.handelFilter} placeholder="Search"/>
                <PatientTable patients={patients} 
                filterText={this.state.filterText}/>
            </div>            
        );
    }
}

export default PatientsPage;
